void rotate_and_invert (unsigned char * original, unsigned char * new,int width, int height)
{
	int x,y;
	int bx, by;

	for (by=0; by<height; by+=8)
	for (bx=0; bx<width; bx+=8)
	for (y=0; y<8; y++)
	for (x=0; x<8; x++)
	*(new + (width * (by + y)) + (bx + x)) = ~*(original + ((height * width) - (width * (by + y) - bx - x)));
}

