	Info->bmiHeader.biSize = sizeof(BITMAPINFOHEADER);
	Info->bmiHeader.biSizeImage = ImageSize.nImageSize;
	Info->bmiHeader.biWidth = ImageSize.nWidth;
	Info->bmiHeader.biXPelsPerMeter = 0;
